// Field Options
!function($){
	"use strict";
	$('body .repeater-content .field-type-select').each(function() {
		var fieldTypeSelected = $(this).val();
		var $fieldRep = $(this).parents('.repeater-content');
		var optionFields = new Array('radio','checkboxes','select');
		if ($.inArray(fieldTypeSelected,optionFields) == 0) {
			$fieldRep.find('.wrap-field .field-options').parents('.wrap-field').removeClass('hide');
		} else {
			$fieldRep.find('.wrap-field .field-options').parents('.wrap-field').addClass('hide');
		}
		
		if (fieldTypeSelected === 'image-upload') {
			$fieldRep.find('.wrap-field .field-sample-image').parents('.wrap-field').removeClass('hide');
		} else {
			$fieldRep.find('.wrap-field .field-sample-image').parents('.wrap-field').addClass('hide');
		}
	});
	
	$('body').on('change','.field-type-select',function(){
		var fieldType = $(this).children("option:selected").val();
		var $thisRepeater = $(this).parents('.repeater-content');
		switch(fieldType) {
			case 'radio':
				$thisRepeater.find('.wrap-field .field-options').parents('.wrap-field').removeClass('hide');
				$thisRepeater.find('.wrap-field .field-sample-image').parents('.wrap-field').addClass('hide');
				break;
			case 'select':
				$thisRepeater.find('.wrap-field .field-options').parents('.wrap-field').removeClass('hide');
				$thisRepeater.find('.wrap-field .field-sample-image').parents('.wrap-field').addClass('hide');
				break;
			case 'checkboxes':
				$thisRepeater.find('.wrap-field .field-options').parents('.wrap-field').removeClass('hide');
				$thisRepeater.find('.wrap-field .field-sample-image').parents('.wrap-field').addClass('hide');
				break;
			case 'image-upload':
				$thisRepeater.find('.wrap-field .field-sample-image').parents('.wrap-field').removeClass('hide');
				$thisRepeater.find('.wrap-field .field-options').parents('.wrap-field').addClass('hide');
				break;
			default:
				$thisRepeater.find('.wrap-field .field-options').parents('.wrap-field').addClass('hide');
				$thisRepeater.find('.wrap-field .field-sample-image').parents('.wrap-field').addClass('hide');
		}
	});
}(jQuery);